mapper.insert:
SELECT 'J-' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '-' || LPAD(SEQ_JRN_CODE.NEXTVAL, 3, '0') FROM dual
INSERT INTO JOURNAL ( JRN_CODE, -- ✅ 시퀀스로 생성된 값 JRN_NO, LINE_NO, JRN_DATE, ACCT_CODE, DC_TYPE, AMOUNT, REMARKS, STATUS, CREATED_AT, CREATED_BY, CUS_CODE, CLOSE_NO, COMPANY_CODE
) VALUES ( #{jrnCode}, -- ✅ selectKey로 먼저 채워진 값 #{jrnNo}, NVL((SELECT MAX(LINE_NO) FROM JOURNAL WHERE JRN_NO = #{jrnNo}), 0) + 1, #{jrnDate}, #{acctCode}, #{dcType}, #{amount}, #{remarks}, 'open', SYSDATE, #{createdBy}, #{cusCode}, #{closeNo}, #{companyCode} )

mapper.update:
		UPDATE JOURNAL
		SET 
		  ACCT_CODE = #{acctCode}
		, DC_TYPE = #{dcType}
		, AMOUNT = #{amount}
		, REMARKS = #{remarks}
		, STATUS = #{status}
		, CUS_CODE = #{cusCode}
		, CLOSE_NO = #{closeNo}
		, CREATED_BY = #{createdBy}  
		 WHERE JRN_CODE = #{jrnCode}
    	AND LINE_NO = #{lineNo}
