mapper.insert:
INSERT INTO EMP (
NAME, DEPT_CODE, POSITION, TITLE, BIRTH, PHONE,
EMAIL, JOIN_DATE, LEAVE_DATE, LEAVE_REASON,
BANK_CODE, ACCOUNT_NO, ACCOUNT_NAME,ZIP_CODE, ADDRESS,ADDRESS_DETAIL, COMPANY_CODE
) VALUES (
#{name}, #{dept_code}, #{position}, #{title}, #{birth}, #{phone},
#{email}, #{joinDate}, #{leaveDate}, #{leaveReason},
#{bankCode}, #{accountNo},#{accountName}, #{zipCode}, #{address},#{addressDetail}, #{companyCode}
)

mapper.update:
UPDATE EMP
NAME = #{name}, DEPT_CODE = #{dept_code}, POSITION = #{position}, TITLE = #{title}, BIRTH = #{birth}, PHONE = #{phone}, EMAIL = #{email}, JOIN_DATE = #{joinDate}, LEAVE_DATE = #{leaveDate}, LEAVE_REASON = #{leaveReason}, BANK_CODE = #{bankCode}, ACCOUNT_NO = #{accountNo}, ZIP_CODE = #{zipCode}, ADDRESS = #{address}, ADDRESS_DETAIL = #{addressDetail}, ACCOUNT_NAME = #{accountName}, COMPANY_CODE = #{companyCode},
WHERE EMP_ID = #{emp_id}
