mapper.update:
UPDATE
    emp_login   
SET
    emp_pw = #{password}
WHERE
    emp_id_no = #{empIdNo}

mapper.select:
SELECT e.EMP_ID, e.NAME, e.DEPT_CODE, e.TITLE, e.EMAIL, e.COMPANY_CODE AS COMPANY_CODE, d.DEPT_NAME, l.COM_NAME FROM emp e JOIN dept d ON e.DEPT_CODE = d.DEPT_CODE JOIN ( SELECT mat_no, MAX(COM_NAME) AS COM_NAME FROM emp_login GROUP BY mat_no ) l ON e.company_code = l.mat_no
d.DEPT_NAME = #{deptName}
AND e.TITLE = #{title}
ORDER BY e.NAME

mapper.update:
UPDATE EMP_LOGIN
SET
    EMP_PW = #{empPw},
    MUST_CHANGE_PW = #{mustChangePw}
WHERE
    EMP_ID = #{empId}

mapper.select:
SELECT COUNT(*)
FROM emp_login
WHERE emp_id = #{empId}

mapper.update:
UPDATE EMP_LOGIN
SET
    com_name = #{comName}  
WHERE
    EMP_ID = #{empId}
